  `ip` varchar(20) NOT NULL DEFAULT '' COMMENT 'ip地址',
  `address` varchar(50) NOT NULL DEFAULT '' COMMENT '地址',
  `ext` varchar(255) NOT NULL DEFAULT '' COMMENT '扩展字段',
  PRIMARY KEY (`id`),
  KEY `type` (`type`),
  KEY `pid` (`pid`),
  KEY `aid` (`aid`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

